mapper.select:
select recruit_id recruitId
from resume_post
where recruit_id = #{recruitId} and resume_id = #{resumeId}

mapper.update:
update resume_post
set state = 'A'
	, create_time=#{createTime}
	, state_time=#{stateTime}
where recruit_id = #{recruitId} and resume_id = #{resumeId}

update resume_post
set state = 'X'
	, state_time=#{stateTime}
where recruit_id = #{recruitId} and resume_id = #{resumeId}
